const commonPrefixes = {};
const commonSuffixes = {};
for (const ability in data) {
  if (data[ability] == "ASI" || data[ability] == "skip2") continue;
  const[prefixes, suffixes] = data[ability];
  for (const i of prefixes) {
    if (i in commonPrefixes) commonPrefixes[i].push(ability);
    else commonPrefixes[i] =[ability]
  }
  for (const i of suffixes) {
    if (i in commonSuffixes) commonSuffixes[i].push(ability);
    else commonSuffixes[i] =[ability]
  }
}
for (const i in commonPrefixes) {
  if (commonPrefixes[i].length == 1) delete commonPrefixes[i];
}
for (const i in commonSuffixes) {
  if (commonSuffixes[i].length == 1) delete commonSuffixes[i];
}
const categoryTables = {}
for (const effect in categories) {
  for (const i of categories[effect]) {
    if (i in categoryTables) categoryTables[i].push(effect);
    else categoryTables[i] = [effect];
  }
}